def event_spent_time(min_val, max_val, weights=(35, 40, 25), sleep=True):
    min_val, max_val = sorted((int(min_val), int(max_val)))
    if max_val == min_val or (max_val - min_val <= 2):
        duration = max_val
    else:
        total_range = max_val - min_val
        one_third = total_range / 3

        low_range = (min_val, min_val + one_third)
        mid_range = (low_range[1], min_val + 2 * one_third)
        high_range = (mid_range[1], max_val)

        rr = random.randint(1, 100)
        if rr <= weights[0]:
            duration = random.randint(*low_range)
        elif rr <= weights[0] + weights[1]:
            duration = random.randint(*mid_range)
        else:
            duration = random.randint(*high_range)
    if sleep:
        time.sleep(duration)
    return duration

