def format_date(_format="%Y-%m-%d %H:%M:%S.%f", timestamp=None, timezone="+0000"):
    """ If you add %z if format then it will add timezone in format e.g. "+00:00" """
    timezone = int(timezone)
    sec = int(abs(timezone) / 100) * 3600 + int(abs(timezone) % 100) * 60
    sec = (-1) * sec if timezone < 0 else sec
    if timestamp is None:
        timestamp = time.time()
    if isinstance(timestamp, (int, long)) and len(str(timestamp)) == 13:
        timestamp = timestamp / 1000.0
    if timezone != "+0000":
        timestamp += sec
    if "%Z" in _format:
        _format = _format.replace("%Z", "{0}".format(timezone))
    if "%z" in _format:
        _format = _format.replace("%z", "{0}:{1}".format(timezone[:3], timezone[3:]))
    if "%f" in _format:
        _f_val = datetime.datetime.utcfromtimestamp(timestamp).strftime("%f")[:3]
        _format = _format.replace("%f", "{0}".format(_f_val))
    if "%F" in _format:
        _format = _format.replace("%F", "%f")
    return datetime.datetime.utcfromtimestamp(timestamp).strftime(_format)

