def get_future_date(_format="%Y-%m-%d", timestamp=time.time(), timezone="+0000", days=0, months=0, years=0, hours=0, minutes=0):
    def get_sec(timezone_):
        timezone_ = int(timezone_)
        sec = int(abs(timezone_) / 100) * 3600 + int(abs(timezone_) % 100) * 60
        return (-1) * sec if timezone_ < 0 else sec

    if timezone != "+0000":
        timestamp += get_sec(timezone)

    if "%Z" in _format:
        _format = _format.replace("%Z", "{0}".format(timezone))
    if "%z" in _format:
        _format = _format.replace("%z", "{0}:{1}".format(timezone[:3], timezone[3:]))
    if "%f" in _format:
        _f_val = datetime.datetime.utcfromtimestamp(timestamp).strftime("%f")[:3]
        _format = _format.replace("%f", "{0}".format(_f_val))
    if "%F" in _format:
        _format = _format.replace("%F", "%f")
    today = datetime.datetime.utcfromtimestamp(timestamp)
    for i in range(months):
        y = (today.month + i) // 12
        month = (today.month + i) % 12
        days = days + calendar.monthrange(today.year + y, month)[1]
    today = datetime.datetime(
        today.year + years, today.month,
        today.day, today.hour, today.minute, today.second, today.microsecond, today.tzinfo
    ) + datetime.timedelta(days=days, hours=hours, minutes=minutes)
    return today.strftime(_format)

